    private void jCbeinditvaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jCbeinditvaActionPerformed
        if (jCbeinditva.isSelected()) {
            if (!egy.isDefekt() & egy.isUzemanyag()) {
                egy = new Auto(egy.isUzemanyag(), true, megadottKerekSzam);
                jTallapot.setText("Beinditva");
                System.out.println(egy.isUzemanyag());
                System.out.println(egy.isBeinditva());
            } else if (!egy.isDefekt() & !egy.isUzemanyag()) {
                egy = new Auto(egy.isUzemanyag(), false, megadottKerekSzam);
                jTallapot.setText("Nincs beinditva. Nincs üzemanyag");
                System.out.println(egy.isUzemanyag());
                System.out.println(egy.isBeinditva());
            } else if (egy.isDefekt() & !egy.isUzemanyag()) {
                egy = new Auto(egy.isUzemanyag(), false, megadottKerekSzam);
                jTallapot.setText("Nincs beinditva. Defekt van");
                System.out.println(egy.isUzemanyag());
                System.out.println(egy.isBeinditva());
            } else if (egy.isDefekt() & !egy.isUzemanyag()) {
                egy = new Auto(egy.isUzemanyag(), false, megadottKerekSzam);
                jTallapot.setText("Nincs beinditva. Defekt van és nincs üzemanyag");
                System.out.println(egy.isUzemanyag());
                System.out.println(egy.isBeinditva());
            }

        } else {
            egy = new Auto(egy.isDefekt(), false, megadottKerekSzam);
            jTallapot.setText("");
        }
    }//GEN-LAST:event_jCbeinditvaActionPerformed

    private void jBtankolActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtankolActionPerformed
        if (!egy.isBeinditva()) {
            if (egy.isUzemanyag()) {
                egy.tankol();
                jTallapot.setText("Tankolva");
                System.out.println(egy.isUzemanyag());
            } else if (!egy.isUzemanyag()) {
                egy.tankol();
                jTallapot.setText("Van üzemanyag");

            }

        } else {
            jTallapot.setText("Nem tudsz tankolni be van inditva");
        }


    }//GEN-LAST:event_jBtankolActionPerformed

    private void jRbUzemanyagActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRbUzemanyagActionPerformed
        if (jRbUzemanyag.isSelected()) {
            if (egy.isUzemanyag()) {
                jTallapot.setText("Van üzemanyag");
                System.out.println(egy.isUzemanyag());
            } else if (!egy.isUzemanyag()) {
                jTallapot.setText("Nincs üzemanyag");
            }

        } else {
            egy = new Auto(false, false);
            jTallapot.setText(" ");
        }
    }//GEN-LAST:event_jRbUzemanyagActionPerformed

    private void jRpDefektActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jRpDefektActionPerformed
        if (jRpDefekt.isSelected()) {
            if (!egy.isDefekt()) {
                JOptionPane.showMessageDialog(rootPane, "Defektett kaptál");
                egy.defektetKap();
                jTallapot.setText("Defekt");
                System.out.println(egy.isDefekt());
            }
        } else {
            egy.kerekCsere();
            jTallapot.setText(" ");
            System.out.println(egy.isDefekt());
        }

    }//GEN-LAST:event_jRpDefektActionPerformed

