    private void JbmegyActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_JbmegyActionPerformed
        if (egy.isBeinditva() & !egy.isDefekt() & egy.isUzemanyag()) {
            egy.megy();
            jTallapot.setText("Megy");
        } else if (!egy.isBeinditva() & !egy.isDefekt() & egy.isUzemanyag()) {
            egy.megy();
            jTallapot.setText("Nem megy, nincs beinidtva");
        } else if (egy.isBeinditva() & egy.isDefekt() & egy.isUzemanyag()) {
            egy.megy();
            jTallapot.setText("Nem megy, defekt van");
        } else if (egy.isBeinditva() & !egy.isDefekt() & !egy.isUzemanyag()) {
            egy.megy();
            jTallapot.setText("Nem megy, nincs üzemanyag");
        } else if (!egy.isBeinditva() & !egy.isDefekt() & !egy.isUzemanyag()) {
            egy.megy();
            jTallapot.setText("Nem megy, nincs semmi");
        }


    }//GEN-LAST:event_JbmegyActionPerformed

    private void jComboBox1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jComboBox1ActionPerformed
        megadottKerekSzam = jComboBox1.getSelectedIndex();
        egy = new Auto(egy.isBeinditva(), egy.isUzemanyag(), megadottKerekSzam);
        jTallapot.setText("Ennyi pótkerék van: " + megadottKerekSzam);


    }//GEN-LAST:event_jComboBox1ActionPerformed

    private void jBtankol1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtankol1ActionPerformed
        if (egy.isDefekt()) {
            if (megadottKerekSzam <= 0) {
                jTallapot.setText("Nincs több pótkerék");
            } else {
                egy.kerekCsere();
                jTallapot.setText("Kerék cserélve");
                megadottKerekSzam--;
                System.out.println(egy.isDefekt());
            }

        } else if (!egy.isDefekt()) {
            jTallapot.setText("Nem kell keleket cserélni");
        }
    }//GEN-LAST:event_jBtankol1ActionPerformed

